import { Template } from 'meteor/templating';
import { FlowRouter } from 'meteor/kadira:flow-router';

import { Products } from '../../api/products/products.js';

import './Market.html';
import '../../api/Products/Server/ProductsPublications.js';
import '../../api/Products/Products.js';


// Components used inside the template

import '../components/ProductItemSmart.js';
import '../components/ProductItemSmart.html';




Template.Media.helpers({
mediaitems() {
   return Products.find();
 }
});

Template.MediaItem.onCreated( function(){

this.mediaitemliked = new ReactiveVar(false);
this.mediaitemcommented = new ReactiveVar(false);
this.mediacreatorfollowing = new Reactivevar(false);

});
